import React, { Component } from "react";
import axios from 'axios'; 
import SearchResults from './SearchResults'
import './style.css';




// const API_KEY = process.env.REACT_APP_API_KEY; 
const lfmKey = '43e2eac1bdb3ea4e9d978121427666';

const eventKey = '7elxdku9GGG5k8j0Xm8KWdANDgecHMV0';

 class SearchBar extends Component {
     state = {
         value: "",
         searchResult:[]
     }

     handleChange = (e) =>{
         this.setState({
             value: e.target.value
         })
     }
     handleSubmit = (e) => {
         e.preventDefault()
         const searchValue = this.state.value
         axios.get(`https://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=${searchValue}&api_key=${lfmKey}`).then(res => {

             this.setState({

                 searchResult: res.data.results
             }) 

 })
     }


  render() {
    return (
    <div className ='container'>
      <div>
          <form onSubmit={this.handleSubmit} className="search-container">
                  <input
                        class="field-container text-dark"
                        type='text'
                        id='search-bar'
                        style={{'border-radius':'5px', width:'30%', justifyContent:'center'}}
                        value={this.state.value}
                        placeholder="Search Images..."
                        onChange={this.handleChange}/>

          <button className="btn-success btn-large" style={{'border-radius': '5px', 'margin-right': '2px'}}type="submit">Search<i class="fa fa-search"></i></button>
          </form>

      </div>
      <div className="jumbotron jumbotron-fluid">
      <div className="container">
        <h1 className="display-4">Fluid jumbotron</h1>
        <p className="lead">This is a modified jumbotron that occupies the entire horizontal space of its parent.</p>
      </div>
    </div>
    </div>
    )
  }
}

export default SearchBar
